INSERT INTO `#__acymailing_mail`  VALUES ('1', 'New Subscriber on your website : {user:email}', '<p>Hello {subtag:name},</p><p>A new user has been created in AcyMailing : </p><blockquote><p>Name : {user:name}</p><p>Email : {user:email}</p><p>IP : {user:ip} </p><p>Subscription : {user:subscription}</p></blockquote>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_created', NULL, NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('2', 'A User unsubscribed from all your lists : {user:email}', '<p>Hello {subtag:name},</p><p>The user {user:name} : {user:email} unsubscribed from all your lists</p><p>Subscription : {user:subscription}</p><p>{survey}</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_unsuball', NULL, NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('3', 'A User unsubscribed : {user:email}', '<p>Hello {subtag:name},</p><p>The user {user:name} : {user:email} unsubscribed from your list</p><p>Subscription : {user:subscription}</p><p>{survey}</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_unsub', NULL, NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('4', 'A User refuses to receive e-mails from your website : {user:email}', '<p>The User {user:name} : {user:email} refuses to receive any e-mail anymore from your website.</p><p>Subscription : {user:subscription}</p><p>{survey}</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_refuse', NULL, NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('5', 'New contact from your website : {user:email}', '<p>Hello {subtag:name},</p><p>A user submitted the form : </p><blockquote><p>Name : {user:name}</p><p>Email : {user:email}</p><p>IP : {user:ip} </p><p>Subscription : {user:subscription}</p></blockquote>', '', '1', NULL,